        }, {
          value: '60',
          label: "60 minutes",
        }, {
          value: '70',
          label: "70 minutes",
        }, {
          value: '80',
          label: "80 minutes",
        }, {
          value: '90',
          label: "90 minutes",
        }, {
          value: '100',
          label: "100 minutes",
        }, {
          value: '110',
          label: "110 minutes",
        }, {
          value: '120',
          label: "120 minutes",
        }
      ],
      pauseSpace: false,
      displayAvailable: true,
      autoAvailable: true,
      rename: '',
      rename_err: false,
      rename_tip: '',
      rule_tip: '',
      docker: '',
      cpuValue: 1,
      memoryValue: 1,
      storageValue: 1,
      gpuValue: 1,
      gpuCheck: false,
      gpuSelect: {
        value: '',
        options: []
      },
      gpuCheckFree: 1,
      max: 72,
      region: '',
      image_name: '',
      imageNameOption: [],
      ssh_key: '',
    })
    const validateInput = (rule, value, callback) => {
      if ((/[^a-zA-Z0-9-._]/g).test(value)) {
        callback(new Error("The space name can only contain ASCII letters, digits, and the characters ., -, and _."));
      } else {
        callback();
      }
    }
    const rules = reactive({
      rename: [
        { required: true, message: ' ', trigger: 'blur' },
        { validator: validateInput, trigger: "blur" }
      ]
    })
    const hardwareOptions = ref([])
    const hardwareLoad = ref(false)
    const machinesLoad = ref(true)
    const sleepVisible = ref(false)
    const forkLoad = ref(false)
    const sleepSelect = ref({})
    const ruleFormRef = ref(null)
    const ruleLoad = ref(false)
    const filesList = ref([])
    const hardwareCPs = ref([])
    const hardwarePrices = reactive({
      data: {
        "gpu": 5,
        "memory": 3,
        "cpu": 2,
        "storage": 1,
        "unit": "SWAN/hour"
      }
    })
    const dialogWidth = ref(document.body.clientWidth < 992 ? '90%' : '800px')
    let tokenAddress = process.env.VUE_APP_SATURN_TOKEN_ADDRESS
    let tokenContract = new system.$commonFun.web3Init.eth.Contract(tokenABI, tokenAddress);
    let paymentContractAddress = process.env.VUE_APP_HARDWARE_ADDRESS
    let paymentContract = new system.$commonFun.web3Init.eth.Contract(SpacePaymentABI, paymentContractAddress)

    async function hardwareFun () {
      const net = await networkEstimate()
      if (!net) return
      hardwareLoad.value = true
      try {
        if (ruleForm.autoAvailable) {
          const paid = (hardwarePrices.data.gpu * ruleForm.gpuValue + hardwarePrices.data.memory * ruleForm.memoryValue + hardwarePrices.data.cpu * ruleForm.cpuValue + hardwarePrices.data.storage * ruleForm.storageValue) * ruleForm.usageTime

          console.log(paid)
          if (paid <= 0) {
            hardwareLoad.value = false
            return
          }


          system.$commonFun.web3Init.eth.sendTransaction({
            from: store.state.metaAddress,
            to: '0xB5aeb540B4895cd024c1625E146684940A849ED9',
            value: String(paid)
          })
            .on('transactionHash', async function (hash) {
              console.log('transactionHash:', hash)
              const getID = await system.$commonFun.web3Init.eth.net.getId()
              const fd = {
                "region": sleepSelect.value.region,
                "node_id": sleepSelect.value.node_id,
                "config": {
                  "vcpu": ruleForm.cpuValue,
                  "memory": ruleForm.memoryValue,
                  "storage": ruleForm.storageValue,
                  "gpu": ruleForm.gpuValue,
                  "gpu_model": ruleForm.gpuSelect.value,
                  "image": ruleForm.image_name,
                  "ssh_key": ruleForm.ssh_key
                },
                "duration": ruleForm.usageTime * 3600,
                "paid": String(paid),
                "tx_hash": hash,
                "chain_id": getID,
                "type": 1,
                "start_in": ruleForm.sleepTime * 60
              }
              const jobsRes = await system.$commonFun.sendRequest(`${process.env.VUE_APP_BASEAPI}spaces/${props.listdata.uuid}/jobs`, 'post', fd)
              if (route.query.uuid) router.push({ name: 'spaceDetail', params: { wallet_address: route.params.wallet_address, name: route.params.name, tabs: 'app' }, query: { uuid: route.query.uuid || '' } })
              else router.push({ name: 'spaceDetail', params: { wallet_address: route.params.wallet_address, name: route.params.name, tabs: 'app' } })
              closePart()
            })
            .on('receipt', function (receipt) {
              console.log('receipt:', receipt)
            })
            .on('confirmation', function (confirmationNumber, receipt) {
              // console.log('confirmation:', confirmationNumber, receipt)
            })
            .on('err', () => {
              console.log('err', err)
              if (err && err.message) system.$commonFun.messageTip('error', err.message)
              closePart()
            })

          return
        }
        if (props.renewButton === 'fork') {
          const forkRes = await forkDuplicate()
          if (forkRes !== 'success') {
            closePart()
            return
          }
        }
        const hardwareInfo = await paymentContract.methods.hardwareInfo(sleepSelect.value.hardware_id).call()
        const pricePerHour = system.$commonFun.web3Init.utils.fromWei(String(hardwareInfo.pricePerHour), 'mwei')
        const approveAmount = (pricePerHour * ruleForm.usageTime).toFixed(6) // usdc is 6 decimal, ensure the amount will not be more than 6
        const wei = system.$commonFun.web3Init.utils.toWei(String(approveAmount), 'mwei')

        let approveGasLimit = await tokenContract.methods
          .approve(paymentContractAddress, wei)
          .estimateGas({ from: store.state.metaAddress })

        const approve_tx = await tokenContract.methods
          .approve(paymentContractAddress, wei)
          .send({
            from: store.state.metaAddress, gasLimit: Math.floor(approveGasLimit * 1.5)
          })

        let payMethod = paymentContract.methods
          .lockRevenue(props.listdata.uuid, sleepSelect.value.hardware_id, ruleForm.usageTime)

        let gasLimit = await payMethod.estimateGas({ from: store.state.metaAddress })
        const tx = await payMethod.send({ from: store.state.metaAddress, gasLimit: Math.floor(gasLimit * 1.5) })
          .on('transactionHash', async (transactionHash) => {
            console.log('transactionHash:', transactionHash)
            await hardwareHash(transactionHash, wei)
            closePart()
            context.emit('handleHard', false, true)
          })
          .on('error', () => hardwareLoad.value = false)
      } catch (err) {
        console.log('err', err)
        if (err && err.message) system.$commonFun.messageTip('error', err.message)
        closePart()
        if (props.renewButton === 'fork') context.emit('handleHard', false, false)
      }
    }

    function closePart () {
      hardwareLoad.value = false
      sleepVisible.value = false
    }

    async function forkDuplicate (type) {
      forkLoad.value = true
      if (!ruleForm.rename) {
        ruleForm.rename_err = true
        forkLoad.value = false
        return false
      } else ruleForm.rename_err = false
      const fd = new FormData();
      fd.append('new_name', ruleForm.rename);
      const forkRes = await system.$commonFun.sendRequest(`${process.env.VUE_APP_BASEAPI}spaces/${route.params.wallet_address}/${route.params.name}/fork`, 'post', fd)
      if (forkRes && forkRes.status === 'success') {
        // const rename = await renameMethod()
        system.$commonFun.messageTip('success', forkRes.message ? forkRes.message : 'Fork successfully!')
        if (type) context.emit('handleHard', false, false)
        router.push({
          name: 'spaceDetail',
          params: { wallet_address: metaAddress.value, name: ruleForm.rename ? ruleForm.rename : route.params.name, tabs: 'card' }
        })
      }
      forkLoad.value = false
      return forkRes.status
    }

    async function renameMethod () {
      if (!ruleForm.rename) return false
      try {
        const formData = new FormData();
        formData.append('name', route.params.name);
        formData.append('is_public', '1'); // public:1, private:0
        formData.append('new_name', ruleForm.rename);

        const renameRes = await system.$commonFun.sendRequest(`${process.env.VUE_APP_BASEAPI}spaces/update`, 'post', formData);
        return true
      } catch{
        return false
      }
    }

    async function networkEstimate () {
      const getID = await system.$commonFun.web3Init.eth.net.getId()
      const list = [2024]
      const getPast = await list.some(t => t === getID)
      if (getPast) return true
      else {
        context.emit('handleHard', false, false, true)
        // await system.$commonFun.messageTip('error', 'Please switch to the network: Mumbai Testnet, OpSwan')
        return false
      }
    }

    async function hardwareHash (tx_hash, approveAmount) {
      const net = await networkEstimate()
      if (!net) return
      const getID = await system.$commonFun.web3Init.eth.net.getId()
      let fd = new FormData()
      fd.append('paid', system.$commonFun.web3Init.utils.fromWei(String(approveAmount), 'ether')) // 授权代币的金额
      // fd.append('paid', approveAmount) // 授权代币的金额
      fd.append('space_name', route.params.name)
      fd.append('cfg_name', sleepSelect.value.hardware_name)
      fd.append('duration', ruleForm.usageTime * 3600)
      fd.append('tx_hash', tx_hash)
      fd.append('chain_id', getID)
      fd.append('region', sleepSelect.value.regionValue)
      fd.append('start_in', ruleForm.sleepTime * 60)
      if (props.renewButton === 'renew') {
        const urlRes = `${process.env.VUE_APP_BASEAPI}spaces/${props.listdata.uuid}/deployment/renew`
        const hardhashRes = await system.$commonFun.sendRequest(urlRes, 'post', fd)
        if (hardhashRes && hardhashRes.status && hardhashRes.message) system.$commonFun.messageTip(hardhashRes.status, hardhashRes.message)
      } else {
        const urlRes = `${process.env.VUE_APP_BASEAPI}space/deployment`
        const hardhashRes = await system.$commonFun.sendRequest(urlRes, 'post', fd)
        if (hardhashRes && hardhashRes.status === 'success') system.$commonFun.messageTip(hardhashRes.status, hardhashRes.message)
        else if (hardhashRes.message) system.$commonFun.messageTip('error', hardhashRes.message)
      }
    }

    function close () {
      if (props.renewButton === 'renew') context.emit('handleHard', false, false)
      else sleepVisible.value = false
    }

    async function sleepChange (row) {
      if (filesList.value.length === 0) {
        if (props.renewButton === 'fork') system.$commonFun.messageTip('warning', 'Can not deploy a empty space!')
        else system.$commonFun.messageTip('warning', 'The space is empty!')
        return
      }
      if (props.renewButton === 'fork' && (!ruleForm.rename || ruleForm.rename_err)) return
      if (row.hardware_status.toLowerCase() !== 'available' && props.renewButton === 'renew') {
        system.$commonFun.messageTip('warning', 'There are no corresponding resources for the current configuration, unable to renew. Please try again later')
        close()
        return
      } else if (row.hardware_status.toLowerCase() !== 'available') return false
      const net = await networkEstimate()
      if (!net) return
      ruleForm.usageTime = 24
      sleepSelect.value = row
      sleepSelect.value.regionOption = await regionList(row.region)
      if (props.renewButton === 'renew') sleepSelect.value.regionValue = props.listdata.activeOrder && props.listdata.activeOrder.region ? props.listdata.activeOrder.region : 'Global'
      else sleepSelect.value.regionValue = row.region && row.region[0] ? "Global" : ''
      ruleForm.sleepTime = sleepSelect.value.hardware_type && sleepSelect.value.hardware_type.indexOf('GPU') > -1 ? '20' : '5'
      if (props.renewButton === 'renew') hardwareLoad.value = false
      sleepVisible.value = true
    }

    async function cpsChange (row) {
      const net = await networkEstimate()
      if (!net) return
      ruleForm.usageTime = 24
      sleepSelect.value = row
      if (sleepSelect.value.resources && sleepSelect.value.resources[0].gpu && sleepSelect.value.resources[0].gpu.gpus) {
        for (let i = 0; i < sleepSelect.value.resources[0].gpu.gpus.length; i++) {
          if (sleepSelect.value.resources[0].gpu.gpus[i].free > 0) {
            ruleForm.gpuSelect.value = sleepSelect.value.resources[0].gpu.gpus[i].model
            break;
          }
        }
      }
      ruleForm.sleepTime = sleepSelect.value.hardware_type && sleepSelect.value.hardware_type.indexOf('GPU') > -1 ? '20' : '5'
      sleepVisible.value = true
    }

    async function listArray (arrayList) {
      let listArr = [
        {
          label: 'CPU',
          list: []
        },
        {
          label: 'GPU',
          list: []
        },
        {
          label: 'AI GPU',
          list: []
        }
      ]
      // arrayList.sort((a, b) => a['hardware_name'].localeCompare(b['hardware_name']))
      arrayList.forEach(async hard => {
        hard.regionOption = await regionList(hard.region)
        hard.regionValue = hard.region && hard.region[0] ? hard.region[0] : ''
        if (hard.hardware_type.toLowerCase() === 'cpu') listArr[0].list.push(hard)
        else if (hard.hardware_type.toLowerCase() === 'gpu') listArr[1].list.push(hard)
        else listArr[2].list.push(hard)
      })
      return listArr
    }

    async function regionList (list) {
      if (!list || !Array.isArray(list) || (list && list.length === 0)) {
        return [];
      }

      let arr = [{
        value: "Global",
        label: "Global"
      }];

      list.forEach(l => {
        arr.push({
          value: l,
          label: l
        });
      });

      return arr;
    }

    async function nameExist () {
      ruleLoad.value = true
      ruleForm.rule_tip = ''
      ruleForm.rename_tip = ruleForm.rename
      if (!ruleForm.rename) ruleForm.rename_err = true
      else if ((/[^a-zA-Z0-9-._]/g).test(ruleForm.rename)) {
        ruleForm.rule_tip = 'The space name can only contain ASCII letters, digits, and the characters ., -, and _.'
        ruleForm.rename_err = true
      } else {
        const existRes = await system.$commonFun.sendRequest(`${process.env.VUE_APP_BASEAPI}spaces/${route.params.wallet_address}/${ruleForm.rename}/exist`, 'get')
        if (existRes && existRes.status === 'success') ruleForm.rename_err = true
        else ruleForm.rename_err = false
      }
      ruleLoad.value = false
    }
    async function init (params) {
      machinesLoad.value = true
      try {
        if (props.renewButton === 'renew') {
          if (props.listdata.activeOrder === null || props.listdata.activeOrder.config === null) return
          ruleForm.usageTime = 24
          sleepSelect.value = {
            hardware_description: props.listdata.activeOrder.config.description,
            hardware_id: props.listdata.activeOrder.config.hardware_id,
            hardware_name: props.listdata.activeOrder.config.name,
            hardware_price: props.listdata.activeOrder.config.price_per_hour,
            hardware_status: "available",
            hardware_type: props.listdata.activeOrder.config.hardware_type,
            region: []
          }
          sleepSelect.value.regionValue = props.listdata.activeOrder && props.listdata.activeOrder.region ? props.listdata.activeOrder.region : 'Global'
          ruleForm.sleepTime = props.listdata.activeOrder.config.hardware_type && props.listdata.activeOrder.config.hardware_type.indexOf('GPU') > -1 ? '20' : '5'
          sleepVisible.value = true
          return
        }

        const machinesRes = await system.$commonFun.sendRequest(`${process.env.VUE_APP_BASEAPI}cp/machines`, 'get')
        if (machinesRes && machinesRes.status === 'success') {
          if (props.renewButton === 'renew') {
            if (props.listdata.activeOrder === null) return
            console.log('machinesRes:', machinesRes.data.hardware.length)
            for (let hard = 0; hard < machinesRes.data.hardware.length; hard++) {
              if (machinesRes.data.hardware[hard].hardware_name === props.listdata.activeOrder.config.name) {
                sleepChange(machinesRes.data.hardware[hard])
                break
              }
            }
          } else hardwareOptions.value = await listArray(machinesRes.data.hardware)
        } else if (machinesRes.message) system.$commonFun.messageTip('error', machinesRes.message)
      } catch{ }
      machinesLoad.value = false
    }
    async function getCps () {
      try {
        const cpsRes = await system.$commonFun.sendRequest(`${process.env.VUE_APP_BASEAPI}cps`, 'get')
        if (cpsRes && cpsRes.status === "success" && cpsRes.data) {
          hardwareCPs.value = cpsRes.data.list || []
          ruleForm.gpuCheckFree = hardwareCPs.value.resources ? hardwareCPs.value.resources[0].gpu.gpus[0].free : 1
        }
      } catch { }
    }
    async function getPrices () {
      try {
        const pricesRes = await system.$commonFun.sendRequest(`${process.env.VUE_APP_BASEAPI}config/prices`, 'get')
        if (pricesRes && pricesRes.status === "success") hardwarePrices.data = pricesRes.data || []
      } catch { }
    }
    async function requestFiles () {
      try {
        const listFilesRes = await system.$commonFun.sendRequest(`${process.env.VUE_APP_BASEAPI}spaces/${route.params.wallet_address}/${route.params.name}/files`, 'get')
        if (listFilesRes && listFilesRes.status === 'success') filesList.value = listFilesRes.data || []
      } catch{ }
    }
    async function getImagesName () {
      try {
        const imagesRes = await system.$commonFun.sendRequest(`${process.env.VUE_APP_BASEAPI}/config/images`, 'get')
        if (imagesRes && imagesRes.status === "success" && imagesRes.data) ruleForm.imageNameOption = imagesRes.data.list || []
        if (sessionStorage.getItem('sshKey')) ruleForm.ssh_key = sessionStorage.getItem('sshKey')
        if (sessionStorage.getItem('imageName')) ruleForm.image_name = sessionStorage.getItem('imageName')
        else if (ruleForm.imageNameOption && ruleForm.imageNameOption.length > 0) ruleForm.image_name = ruleForm.imageNameOption[0].tag
      } catch { }
    }
    async function availableChange () {
      // console.log(ruleForm.displayAvailable)
    }
    async function autoChange () {
      // console.log(ruleForm.displayAvailable)
    }
    const gpuChange = (value) => {
      ruleForm.gpuCheckFree = value
    }
    let getnetID = NaN
    onMounted(async () => {
      ruleForm.rename = route.params.name
      ruleForm.rename_tip = route.params.name
      getnetID = await system.$commonFun.web3Init.eth.net.getId()
      // paymentEnv()
      requestFiles()
      init()
      getCps()
      getPrices()
      nameExist()
      getImagesName()
    })
    return {
      route,
      accessName,
      metaAddress,
      metaSmallAddress,
      bodyWidth,
      system,
      props,
      ruleForm,
      rules,
      sleepVisible,
      sleepSelect,
      dialogWidth,
      hardwareOptions,
      hardwareLoad,
      machinesLoad,
      forkLoad, ruleFormRef, ruleLoad, hardwareCPs, hardwarePrices,
      sleepChange, hardwareFun, close, forkDuplicate, nameExist, availableChange, autoChange, cpsChange, gpuChange
    }
  }
})
